update.foreach:
update sys_department
business_id=#{item.businessId}, name=#{item.orgFullName}, short_name=#{item.orgName}, english_name=#{item.orgEngName}, code=#{item.orgCode}, business_unit=#{item.bu}, department_leader=#{item.orgOwner}, mail=#{item.email}, parent_id=#{item.parentId}, update_time=now()
where business_id = #{item.businessId}
