update.foreach:
UPDATE sys_user
user_name=#{item.accountName}, full_name=#{item.chsName}, nick_name=#{item.adAccount}, sex=#{item.gender}, grade=#{item.gradeType}, position=#{item.positionName}, phone=#{item.mobilePhone}, mail=#{item.email}, image_url=#{item.imageUrl}, department_id=#{item.orgId}, update_time=now(), update_by=#{item.updateBy}
WHERE user_name = #{item.accountName}
