mapper.delete:
        DELETE FROM sys_role_menu WHERE role_id = #{roleId}

        
            AND menu_id IN (
            SELECT m.id FROM (
            SELECT srm.menu_id AS id FROM sys_user su
            INNER JOIN sys_user_role sur ON sur.user_id = su.id
            INNER JOIN sys_role sr ON sr.id = sur.role_id
            INNER JOIN sys_role_menu srm ON srm.role_id = sr.id
            WHERE su.id = #{userId}
            )m
            )
