mapper.delete:
        DELETE FROM sys_user_role
        WHERE user_id = #{userId}

        
            AND role_id IN (
            SELECT r.id FROM (
            SELECT sur.role_id AS id
            FROM sys_user su
            INNER JOIN sys_user_role sur ON sur.user_id = su.id
            WHERE sur.user_id = #{createBy}
            )r
            )
